import { createContext, useEffect, useReducer } from "react";

const INITIAL_STATE = {
  user: JSON.parse(localStorage?.getItem("user")) || null,
  loading: false,
  error: null,
};

export const AuthContext = createContext(INITIAL_STATE);

const AuthReducer = async (state, action) => {
  switch (action.type) {
    case "ERROR_DEFAULT":
      return {
        ...state,
        error: null,
      };
    case "LOGIN_START":
      return {
        user: null,
        loading: true,
        error: null,
      };
    case "LOGIN_SUCCESS":
      return {
        user: action.payload,
        loading: false,
        error: null,
      };
    case "LOGIN_FAILURE":
      return {
        user: null,
        loading: false,
        error: action.payload,
      };
    case "LOGOUT_USER":
      localStorage.removeItem("user")
      return {
        user: null,
        loading: false,
        error: null,
      };
    default:
      return state;
  }
};

export const AuthContextProvider = ({ children }) => {
  const [{ user, loading, error }, dispatch] = useReducer(
    AuthReducer,
    INITIAL_STATE
  );

  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(user));
  }, [user]);

  const defaultErrorHandler = () => {
    dispatch({ type: "ERROR_DEFAULT" });
  };

  return (
    <AuthContext.Provider
      value={{ user, loading, error, dispatch, defaultErrorHandler }}
    >
      {children}
    </AuthContext.Provider>
  );
};
