import {
    FormControl,
    FormLabel,
    Input, Heading, Button, Spinner
  } from '@chakra-ui/react'
import { useCallback, useContext, useEffect, useState } from 'react'
import { AuthContext } from '../../context/context';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const INITIAL_STATE = {
    username: '',
    password: '',
}

function SignIn() {
    const [formFields, setFormFields] = useState(INITIAL_STATE)
    const {username, password} = formFields;
    const {user, loading, error, dispatch} = useContext(AuthContext)
    const navigate = useNavigate()

    // useEffect(() => {
    //     console.log("USER : ", user)
    //     if (user) navigate("/todo");
    // }, [])

    const onChangeHandler = useCallback((e) => {
        const name = e.target.name;
        const value = e.target.value;
        setFormFields({...formFields, [name]: value})
    }, [formFields])

    const onSubmitHandler = async () => {
        dispatch({type: "LOGIN_START"});
        axios.post("http://www.localhost:3001/auth/login", {
                ...formFields
            }, { headers: {"Access-Control-Allow-Origin": "http://localhost:3000"}}, {withCredentials: true}).then( response => {
                const data = response.data.details;
                console.log(data)
                dispatch({type: "LOGIN_SUCCESS", payload: data})
                navigate("/todo")
                }
                ).catch( err => {
                    dispatch({type: "LOGIN_FAILURE", paylaod: err})
                })
    }

    const disable = (username === '' || password ==='')
  return (
    <>
        <FormControl width={'400px'}>
        <Heading size={"lg"}>Sign In</Heading>
        <FormLabel mt='20px'>Username</FormLabel>
        <Input type='text' name="username" value={username} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Password</FormLabel>
        <Input type='password' name="password" value={password} onChange={onChangeHandler} required/>
        <Button isDisabled={disable} colorScheme='teal' size='md' mt={'20px'} onClick={onSubmitHandler}>
        {  loading?
            <Spinner size={"md"}/>
            :
            'Sign In'
        }
        </Button>
        </FormControl>
        </>
  )
}

export default SignIn;
