import { SimpleGrid } from '@chakra-ui/react';
import React, { memo } from 'react'
import CardTodoComponent from '../card-todo/card-todo.component';
import { useNavigate } from 'react-router-dom';

function CardList({todos}) {

  const navigate = useNavigate

  const editTodoHandler = (id) => {
    // goto edit page with todo id
    navigate("/todo/edit", { _id: id });
  };

  const deleteTodoHandler = (id) => {
    navigate("/todo/delete", { _id: id });
    // goto edit page with todo id
  };

    return (
        <SimpleGrid
          columns={3}
          minChildWidth={"280px"}
          maxWidth={"480px"}
          spacing={10}
          mt={"100px"}
        >
          {todos &&
            todos?.length !== 0 &&
            todos?.map(({ name, description, _id }) => (
              <CardTodoComponent key = {_id}
                name={name}
                description={description}
                _id={_id}
                editHandler={editTodoHandler}
                deleteHandler={deleteTodoHandler}
              />
            ))}
        </SimpleGrid>
        )
    }
    
export default memo(CardList);
