import { useLocation, useNavigate, useParams } from "react-router-dom";
import {
  Box,
  Button,
  Flex,
  FormControl,
  FormLabel,
  Input,
  Spacer,
  Spinner,
  Textarea,
  useToast,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";
import Navbar from "../components/navbar/navbar.component";
import useFetch from "../hooks/useFetch.hook";
import instance from "../utils/axios.instance";

export default function EditTodo() {
    const { _id } = useLocation();
    console.log(_id);
  const navigate = useNavigate()
  const toast = useToast();
  const { data, loading, error, retry } = useFetch(`/todo/${_id}`, "get");
  const [formFields, setFormFields] = useState({ name: "", description: "" });
  const { name, description } = formFields;

  useEffect(() => {
    if (data && data?.todo) {
      setFormFields({
        name: data.todo.name,
        description: data.todo.description,
      });
    }
  }, [data]);

  const updateTodoHandler = async () => {
    try {
      await instance({
        url: "/todo/update",
        method: "patch",
        data: { _id, ...formFields },
      });
      toast({
        title: "Update!",
        description: `Todo ${name} updated successfully.`,
        status: "success",
        duration: 1000,
        isClosable: true,
      });
      setTimeout(() => {
        navigate("/todo")
      }, 1000)

    } catch (error) {
      toast({
        title: "Update Failed!",
        description: `Todo ${name} failed to update.`,
        status: "error",
        duration: 2000,
        isClosable: true,
      });
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormFields({ ...formFields, [name]: value });
  };

  return (
    <Flex flexDirection={"column"} m={"150px"} mt={"20px"}>
      <Navbar />
      <Spacer />
      <Box>
        {loading && <Spinner size={"xl"} color="red" />}
        {data && data?.todo ? (
          <FormControl>
            <FormLabel>Name</FormLabel>
            <Input
              type="text"
              name="name"
              value={name}
              onChange={handleInputChange}
            />
            <FormLabel>description</FormLabel>
            <Textarea
              type="text"
              name="description"
              value={description}
              onChange={handleInputChange}
            />
            <Button colorScheme="teal" onClick={updateTodoHandler}>
              Update
            </Button>
          </FormControl>
        ) : null}
      </Box>
    </Flex>
  );
}
