import React, { memo, useCallback, useState } from 'react'
import {
    FormControl,
    FormLabel,
    Input, Heading, Button, FormHelperText, FormErrorMessage
  } from '@chakra-ui/react'

const INITIAL_STATE = {
    username: '',
    password: '',
    confirmPassword: '',
    email: ''
}

function SignUp() {
    const [formFields, setFormFields] = useState(INITIAL_STATE)
    const {username, password, confirmPassword, email} = formFields;
    const {isError, setIsError} = useState({message})

    const onChangeHandler = useCallback((event) => {
        const value = event.target.value;
        const name = event.target.name;
        setFormFields({...formFields, [name]: value})
    }, [formFields])

    const onSubmitHandler = () => {

    }

    const disable = (password !== confirmPassword || username.length < 3 || password.length < 6 || !email.match(/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i))
  return (
    <FormControl width={'400px'}>
        <Heading size={"lg"}>Sign In</Heading>
        <FormLabel mt='20px'>Username</FormLabel>
        <Input type='text' name="username" value={username} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Password</FormLabel>
        <Input type='password' name="password" value={password} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Confirm Password</FormLabel>
        <Input type='password' name="confirmPassword" value={confirmPassword} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Email</FormLabel>
        <Input type='email' name="email" value={email} onChange={onChangeHandler} isRequired={true}/>
        <Button isDisabled={disable} colorScheme='teal' size='md' mt={'20px'} onClick={onSubmitHandler}>
        Sign In
        </Button>
        {!isError ? (
            <FormHelperText>
              Enter the email you'd like to receive the newsletter on.
            </FormHelperText>
          ) : (
            <FormErrorMessage>Email is required.</FormErrorMessage>
          )}
    </FormControl>

    // <div className='sign-in-container'>
    //   <h2>Sign Up</h2>
    //   <form onSubmit={onSubmitHandler}>
    //   <input name="username" type='text' placeholder='Username' required onChange={changeHandler}/>
    //   <input name="email" type='email' placeholder='Email' required onChange={changeHandler}/>
    //   <input name="password" type='password' placeholder='Password' required onChange={changeHandler}/>
    //   <input name="confirmPassword" type='password' placeholder='Confirm Password' required onChange={changeHandler}/>
    //   <input type='submit' placeholder='Sign Up' />
    //   </form>
    //   </div>
  )
}

export default memo(SignUp);
