import { Box, Button, ButtonGroup, Flex, Heading, SimpleGrid, Spacer, Spinner } from "@chakra-ui/react";
import { useContext } from "react";
import { AuthContext } from "../context/context";
import { TodoContext } from "../context/todo";
import useFetch from "../hooks/useFetch.hook";
import { useNavigate } from "react-router-dom";

export default function ToDo() {
    const navigate = useNavigate();
    const {user, dispatch: authDispatch} = useContext(AuthContext)
    const {data, loading, error, retry, reFetch} = useFetch("/todo/", "get");

    console.log(data)
    console.log(user)

    const signInClick = () => {
        navigate("/")
    }
  return (
    <Flex flexDirection={'column'} m={'150px'} mt={'20px'}>
        <Box>
        <Flex minWidth='max-content' alignItems='center' gap='2'>
        <Box p='2'>
        <Heading size='md'>{user?.username || "TODO"}'s TODO</Heading>
        </Box>
        <Spacer />
        {!user && <ButtonGroup gap='2'>
        <Button colorScheme='teal' size={'md'} onClick={signInClick}>Log In | Sign Up</Button>
        </ButtonGroup>}
        </Flex>
        </Box>
        <Spacer />
        <Box>
        { !user && <Heading mt={'100px'}>Sign In/Sign Up To Use ToDo App</Heading>}
        {loading && 
        <Spinner color='red.500' mt={'100px'} size='xl' textAlign={'center'}/>
        }

        {
            data.length !== 0 && 
            <SimpleGrid columns={2} spacing={10} mt={'100px'}>
            <Card align="left">
            <CardHeader>
              <Heading size='md'> Customer dashboard</Heading>
            </CardHeader>
            <CardBody>
              <Text>View a summary of all your customers over the last month.</Text>
            </CardBody>
            <CardFooter>
              <Button colorScheme='blue'>View here</Button>
            </CardFooter>
          </Card>
            </SimpleGrid>
        }
        </Box>
    </Flex>
  )
}
