import { Box, Flex, Heading, Spacer, Spinner } from "@chakra-ui/react";
import { useContext, useEffect } from "react";
import { AuthContext } from "../context/context";
import useFetch from "../hooks/useFetch.hook";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/navbar/navbar.component";
import CardList from "../components/card-list/card-list.component";
import { TodoContext } from "../context/todo";

export default function ToDo() {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const { todos, dispatch } = useContext(TodoContext);
  const { data, loading } = useFetch("/todo/", "get");

  useEffect( () => {
    if(data?.todos.length > 0 ) {
      
    }
  }, [data])
  const editTodoHandler = (id) => {
    // goto edit page with todo id
    navigate("/todo/edit", { _id: id });
  };

  const deleteTodoHandler = (id) => {
    navigate("/todo/delete", { _id: id });
    // goto edit page with todo id
  };

  return (
    <Flex flexDirection={"column"} m={"150px"} mt={"20px"}>
      <Navbar />
      <Spacer />
      <Box>
        {!user && (
          <Heading mt={"100px"}>Sign In/Sign Up To Use ToDo App</Heading>
        )}
        {loading && (
          <Spinner
            color="red.500"
            mt={"100px"}
            size="xl"
            textAlign={"center"}
          />
        )}
        <CardList
          todos={todos}
          editTodoHandler={editTodoHandler}
          deleteTodoHandler={deleteTodoHandler}
        />
      </Box>
    </Flex>
  );
}
