import {useEffect, useState} from "react"
import axios from "axios"

export default function useFetch(url, method, url_data) {
    console.log(url)
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(true)
    const [error, setError] = useState(false)
    const [retry, setRetry] = useState(1)

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true)

        var config_body_headers;
        if(url_data) {
            config_body_headers = {
                url: url,
                method: method,
                withCredentials: true,
                baseURL: 'http://www.localhost:3001'
            },
             {...url_data}
            ,
            {headers: {
                "Access-Control-Allow-Origin": 'http://localhost:3000',
            }}
        } else {
            config_body_headers = {
                url: url,
                method: method,
                withCredentials: true,
                baseURL: 'http://www.localhost:3001'
            }
            , {headers: {
                "Access-Control-Allow-Origin": 'http://localhost:3000',
            }}
        }
            
            try{
                const res = await axios(config_body_headers)
                setData(res.data)
            } catch(err){
                setError(err)
            }
            setLoading(false)
        };
        fetchData();
    }, [])

  const reFetch = async () => {
    setLoading(true)
    setRetry(retry+1)
    try{
        const res = await axios.get(url)
        setData(res.data)
    } catch(err){
        setError(err)
    }
    setLoading(false)
  }

  return {data, loading, retry, error, reFetch}
}
