import {
    FormControl,
    FormLabel,
    FormErrorMessage,
    FormHelperText,
    Input, Heading
  } from '@chakra-ui/react'
import { useState } from 'react'

const INITIAL_STATE = {
    username: '',
    password: '',
}

export default function SignIn() {
    const [formFields, setFormFields] = useState('')
    const {username, password} = formFields;

    const onChangeHandler = (e) => {
        const name = e.target.name;
        const value = e.target.value
        setFormFields({...formFields, [name]: value})
    } 
  
    const isError = (username === '' || password === "");
  return (
    // <FormControl isInvalid={isError}>
    // <FormLabel>Email</FormLabel>
    // <Input type='email' value={input} onChange={handleInputChange} />
    // {!isError ? (
    //   <FormHelperText>
    //     Enter the email you'd like to receive the newsletter on.
    //   </FormHelperText>
    // ) : (
    //   <FormErrorMessage>Email is required.</FormErrorMessage>
    // )}
//   </FormControl>
      <FormControl width={'400px'}>
        <Heading size={""}>Sign In</Heading>
        <FormLabel mt='20px'>Username</FormLabel>
        <Input type='text' name="username" value={username} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Password</FormLabel>
        <Input type='password' name="password" value={password} onChange={onChangeHandler} required/>
    </FormControl>
  )
}
