import { createContext, useEffect, useReducer } from "react";

const INITIAL_STATE = {
  user: {},
  loading: false,
  error: null,
};

export const AuthContext = createContext(INITIAL_STATE);

const AuthReducer = async (state, action) => {
  const {type, payload} = action;
  switch (type) {
    case "ERROR_DEFAULT":
      return {
        ...state,
        error: null,
      };
    case "LOGIN_START":
      return {
        user: null,
        loading: true,
        error: null,
      };
    case "LOGIN_SUCCESS":
      return {
        user: payload,
        loading: false,
        error: null,
      };
    case "LOGIN_FAILURE":
      return {
        user: null,
        loading: false,
        error: payload,
      };
    case "LOGOUT_USER":
      return {
        user: null,
        loading: false,
        error: null,
      };
    default:
      return state;
  }
};

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(
    AuthReducer,
    INITIAL_STATE
  );

  useEffect(() => {
    console.log("STATE : ")
    const {user} = state
    if (user) {
      localStorage.setItem("user", JSON.stringify(user));
    } else {
      localStorage.removeItem("user")
    }
  }, [state]);

  const defaultErrorHandler = () => {
    dispatch({ type: "ERROR_DEFAULT" });
  };

  return (
    <AuthContext.Provider
      value={{ state, dispatch, defaultErrorHandler }}
    >
      {children}
    </AuthContext.Provider>
  );
};
