import {
  Box,
  Button,
  ButtonGroup,
  Flex,
  Heading,
  SimpleGrid,
  Spacer,
  Spinner,
} from "@chakra-ui/react";
import { useContext } from "react";
import { AuthContext } from "../context/context";
import { TodoContext } from "../context/todo";
import useFetch from "../hooks/useFetch.hook";
import { useNavigate } from "react-router-dom";
import CardTodo from "../components/card-todo/card-todo.component";
import Navbar from "../components/navbar/navbar.component";

export default function ToDo() {
  const navigate = useNavigate();
  const { user} = useContext(AuthContext);
  const { data, loading, error, retry, reFetch } = useFetch("/todo/", "get");

  const { todos } = data;
  console.log(data.todos);

  const editTodoHandler = (id) => {
    // goto edit page with todo id
    navigate("/todo/edit", { _id: id });
  };

  const deleteTodoHandler = (id) => {
    navigate("/todo/delete", { _id: id });
    // goto edit page with todo id
  };


  return (
    <Flex flexDirection={"column"} m={"150px"} mt={"20px"}>
      <Navbar />
      <Spacer />
      <Box>
        {!user && (
          <Heading mt={"100px"}>Sign In/Sign Up To Use ToDo App</Heading>
        )}
        {loading && (
          <Spinner
            color="red.500"
            mt={"100px"}
            size="xl"
            textAlign={"center"}
          />
        )}

        <SimpleGrid
          columns={3}
          minChildWidth={"280px"}
          maxWidth={"480px"}
          spacing={10}
          mt={"100px"}
        >
          {todos &&
            todos?.length !== 0 &&
            todos?.map(({ name, description, _id }) => (
              <CardTodo key = {_id}
                name={name}
                description={description}
                _id={_id}
                editHandler={editTodoHandler}
                deleteHandler={deleteTodoHandler}
              />
            ))}
        </SimpleGrid>
      </Box>
    </Flex>
  );
}
