import { useLocation, useNavigate } from "react-router-dom";
import useFetch from "../../hooks/useFetch.hook";
import {
  Button,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  useToast,
} from "@chakra-ui/react";
import { memo, useEffect, useState } from "react";

function EditTodoForm({name, description, inputHandler, updateHandler}) {

  return (
          <FormControl>
            <FormLabel>Name</FormLabel>
            <Input
              type="text"
              name="name"
              value={name}
              onChange={inputHandler}
            />
            <FormLabel>description</FormLabel>
            <Textarea
              type="text"
              name="description"
              value={description}
              onChange={inputHandler}
            />
            <Button colorScheme="teal" onClick={updateHandler}>
              Update
            </Button>
          </FormControl>
        )
}

export default memo(EditTodoForm)
