import { Box, Button, ButtonGroup, Flex, Heading, SimpleGrid, Spacer } from "@chakra-ui/react";
import { useContext } from "react";
import { AuthContext } from "../context/context";
import { TodoContext } from "../context/todo";
import useFetch from "../hooks/useFetch.hook";

export default function ToDo() {
    const {user, dispatch: authDispatch} = useContext(AuthContext)
    const {todos, todo, dispatch, dispatch: todoDispatch} = useContext(TodoContext)
    const {data, loading, error, reFetch} = useFetch("localhost:3001/");
    
  return (
    <Flex flexDirection={'column'} m={'150px'} mt={'20px'}>
        <Box>
        <Flex minWidth='max-content' alignItems='center' gap='2'>
        <Box p='2'>
        <Heading size='md'>{user?.username || "TODO"}'s TODO</Heading>
        </Box>
        <Spacer />
        {!user && <ButtonGroup gap='2'>
        <Button colorScheme='teal'>Sign Up</Button>
        <Button colorScheme='teal'>Log in</Button>
        </ButtonGroup>}
        </Flex>
        </Box>
        <Spacer />
        <Box>
        { !user ? <Heading mt={'100px'}>Sign In/Sign Up To Use ToDo App</Heading> :
            <SimpleGrid columns={2} spacing={10} mt={'100px'}>
                <Box bg='tomato' height='250px'></Box>
                <Box bg='tomato' height='250px'></Box>
                <Box bg='tomato' height='250px'></Box>
                <Box bg='tomato' height='250px'></Box>
                <Box bg='tomato' height='250px'></Box>
            </SimpleGrid>
        }
        </Box>
    </Flex>
  )
}
