import { SimpleGrid } from "@chakra-ui/react";
import React, { memo, useCallback, useContext } from "react";
import CardTodoComponent from "../card-todo/card-todo.component";
import { useNavigate } from "react-router-dom";
import { TodoContext } from "../../context/todo";
import axios from "axios";
import instance from "../../utils/axios.instance";
import { useToast } from '@chakra-ui/react'

function CardList() {
  const navigate = useNavigate();
  const toast = useToast();
  const { todos, dispatch } = useContext(TodoContext);

  const editTodoHandler = useCallback((id) => {
    // goto edit page with todo id
    navigate("/todo/edit", { _id: id });
  }, []);

  const deleteTodoHandler = useCallback(async (id, name) => {
    const oldTodos = todos;
    const newTodos = todos.filter( ({_id}) => _id !== id);
    dispatch({type: "SET_TODOS", payload: newTodos})
    try{
      await instance({
        url: '/todo/delete',
        method: 'delete',
        params: {_id: id}
      })
      toast({
        title: `Deleted!`,
        description: "Deleted ",
        status: 'error',
        duration: 5000,
        isClosable: true,
      })
    } catch(err) {
      dispatch({type: "SET_TODOS", payload: oldTodos})
    }
    // goto edit page with todo id
  }, []);

  return (
    <SimpleGrid
      columns={3}
      minChildWidth={"280px"}
      maxWidth={"480px"}
      spacing={10}
      mt={"100px"}
    >
      {todos &&
        todos?.length !== 0 &&
        todos?.map(({ name, description, _id }) => (
          <CardTodoComponent
            key={_id}
            name={name}
            description={description}
            _id={_id}
            editHandler={editTodoHandler}
            deleteHandler={deleteTodoHandler}
          />
        ))}
    </SimpleGrid>
  );
}

export default memo(CardList);
