import { useLocation, useNavigate, useParams } from "react-router-dom";
import {
  Box,
  Flex,
  Heading,
  Spacer,
  Spinner,
  useToast,
} from "@chakra-ui/react";
import { useCallback, useEffect, useState } from "react";
import Navbar from "../components/navbar/navbar.component";
import useFetch from "../hooks/useFetch.hook";
import instance from "../utils/axios.instance";
import EditTodoForm from "../components/edit-todo-form/edit-todo-form.component";

export default function EditTodo() {
  const { state } = useLocation();
  console.log(state);
  const { _id } = state;
  const navigate = useNavigate();
  const toast = useToast();
  const { data, loading, error, retry } = useFetch(`/todo/${_id}`, "get");
  const [formFields, setFormFields] = useState({ name: "", description: "" });
  const { name, description } = formFields;

  useEffect(() => {
    if (data && data?.todo) {
      setFormFields({
        name: data.todo.name,
        description: data.todo.description,
      });
    }
  }, [data]);

  const updateTodoHandler = useCallback(async () => {
    try {
      await instance({
        url: "/todo/update",
        method: "patch",
        data: { _id, ...formFields },
      });
      toast({
        title: "Update!",
        description: `Todo ${name} updated successfully.`,
        status: "success",
        duration: 1000,
        isClosable: true,
      });
      setTimeout(() => {
        navigate("/todo");
      }, 1000);
    } catch (error) {
      toast({
        title: "Update Failed!",
        description: `Todo ${name} failed to update.`,
        status: "error",
        duration: 2000,
        isClosable: true,
      });
    }
  }, []);

  const handleInputChange = useCallback((event) => {
    const { name, value } = event.target;
    setFormFields({ ...formFields, [name]: value });
  });

  return (
    <Flex flexDirection={"column"} m={"150px"} mt={"20px"} spacing='100px'>
      <Navbar />
      <Spacer />
      <Heading size='lg' margin={'auto'} mt={'50px'}>Edit Todo {name}</Heading>
      <Box>
      {loading && <Spinner size={"xl"} color="red" />}
      {data && data?.todo ? (
        <EditTodoForm name={name} description={description} inputChange= {handleInputChange} updateHandler = {updateTodoHandler} />
        ) : null}
      </Box>
    </Flex>
  );
}
