import {
    FormControl,
    FormLabel,
    Input, Heading, Button, Spinner, FormErrorMessage, FormHelperText
  } from '@chakra-ui/react'
import { useCallback, useContext, useState } from 'react'
import { AuthContext } from '../../context/context';
import { useNavigate } from 'react-router-dom';
import instance from '../../utils/axios.instance';

const INITIAL_STATE = {
    username: '',
    password: '',
}

function SignIn() {
    const [formFields, setFormFields] = useState(INITIAL_STATE)
    const {username, password} = formFields;
    const {loading, error, dispatch} = useContext(AuthContext)
    const navigate = useNavigate()
    console.log(error)
    const onChangeHandler = useCallback((e) => {
        const fieldName = e.target.name;
        const value = e.target.value;
        setFormFields({...formFields, [fieldName]: value})
    }, [formFields])

    const onSubmitHandler = async (e) => {
        dispatch({type: "LOGIN_START"});
        instance(
            {
                method: 'post',
                url: "/auth/login",
                data: {...formFields},
              }).then( response => {
                const data = response.data.details;
                console.log(data)
                dispatch({type: "LOGIN_SUCCESS", payload: data})
                navigate("/todo")
                }
                ).catch( err => {
                  const {response: {data: {message}}} = err;
                    dispatch({type: "LOGIN_FAILURE", payload: message})
                })
    }

    const disable = (username === '' || password ==='')
  return (
    <>
        <FormControl width={'400px'} isInvalid={error}>
        <Heading size={"lg"}>Sign In</Heading>
        <FormLabel mt='20px'>Username</FormLabel>
        <Input type='text' name="username" value={username} onChange={onChangeHandler} required/>
        <FormLabel mt='20px'>Password</FormLabel>
        <Input type='password' name="password" value={password} onChange={onChangeHandler} required/>
        {error && (
        <FormErrorMessage>{error}</FormErrorMessage>) }
        <Button isDisabled={disable} colorScheme='teal' size='md' mt={'20px'} onClick={onSubmitHandler}>
        {  loading?
            <Spinner size={"md"}/>
            :
            'Sign In'
        }
        </Button>
        </FormControl>
        </>
  )
}

export default SignIn;
