import { Box, Button, ButtonGroup, Card, CardBody, CardFooter, CardHeader, Flex, Heading, SimpleGrid, Spacer, Spinner, Text } from "@chakra-ui/react";
import { useContext } from "react";
import { AuthContext } from "../context/context";
import { TodoContext } from "../context/todo";
import useFetch from "../hooks/useFetch.hook";
import { useNavigate } from "react-router-dom";
import CardTodo from "../components/card-todo/card-todo.component";

export default function ToDo() {
    const navigate = useNavigate();
    const {user, dispatch: authDispatch} = useContext(AuthContext)
    const {data, loading, error, retry, reFetch} = useFetch("/todo/", "get");

    const {todos} = data; 
    console.log(data.todos)
    console.log(user)

    const signInClick = () => {
        navigate("/")
    }

    const logOutHandler = () => {
      authDispatch('LOGOUT')
    }

    const editTodoHandler = (id) => {
      // goto edit page with todo id
      navigate("/todo/edit", {_id: id})
    }
    
    const deleteTodoHandler = (id) => {
      navigate("/todo/delete", {_id: id})
      // goto edit page with todo id
    }

    const creatTodoHandler = () => {
      navigate("/todo/create")
    }

  return (
    <Flex flexDirection={'column'} m={'150px'} mt={'20px'}>
        <Box>
        <Flex minWidth='max-content' alignItems='center' gap='2'>
        <Box p='2'>
        <Heading size='md'>{user?.username || "TODO"}'s TODO</Heading>
        </Box>
        <Spacer />
        {!user ? <ButtonGroup gap='2'>
        <Button colorScheme='teal' size={'md'} onClick={signInClick}>Log In | Sign Up</Button>
        </ButtonGroup> 
        : 
        <ButtonGroup>
        <Button colorScheme={"teal"} onClick={creatTodoHandler}>Create ToDo</Button>
        <Button colorScheme={"gray"} color={'red.500'} onClick={logOutHandler}>Log Out</Button>
        </ButtonGroup>
      }
        </Flex>
        </Box>
        <Spacer />
        <Box>
        { !user && <Heading mt={'100px'}>Sign In/Sign Up To Use ToDo App</Heading>}
        {loading && 
        <Spinner color='red.500' mt={'100px'} size='xl' textAlign={'center'}/>
        }

        <SimpleGrid columns={3} minChildWidth={'280px'} maxWidth={'480px'} spacing={10} mt={'100px'}>
        {
            (todos && todos?.length !== 0) && todos?.map( ({name, description, _id}) => 
              <CardTodo name={name} description={description} _id={_id} editHandler={editTodoHandler} deleteHandler = {deleteTodoHandler} />
              )
        }
            </SimpleGrid>
        </Box>
    </Flex>
  )
}
